import { Component } from '@angular/core';
//import { AuxService } from '../services/aux.service';

@Component({
  selector: 'app-barra',
  templateUrl: './barra.component.html',
  styleUrls: ['./barra.component.css']
})
export class BarraComponent {
  [x: string]: any;
  //constructor(private AuxService: AuxService) {
    //this.AuxService.loadScript();
  //}
  items: Array<{ icon: string; text: string }> = [
    { icon: 'fas fa-home', text: 'Home' },
    { icon: 'fas fa-user', text: 'Profile' },
    { icon: 'fas fa-comment', text: 'Message' },
    { icon: 'fas fa-camera-retro', text: 'Photos' },
    { icon: 'fas fa-cog', text: 'Settings' }
  ];

  activeIndex: number = -1;

  setActive(index: number): void {
    this.activeIndex = index;
  }
}

<body>
    <div *ngFor="let item of items; let i = index" class="navigation" [ngClass]="{ active: i === activeIndex }"
    (click)="setActive(i)">
    <ul>
      <li *ngFor="let item of items; let i = index" class="list" [ngClass]="{ active: i === activeIndex }" (click)="setActive(i)" class="list active">
        <a href="#">
          <span class="icon"><i class="fas fa-home"></i></span>
          <span class="text">Home</span>
        </a>
      </li>
      <li class="list">
        <a href="#">
          <span class="icon"><i class="fas fa-user"></i></span>
          <span class="text">Profile</span>
        </a>
      </li>
      <li class="list">
        <a href="#">
          <span class="icon"><i class="fas fa-comment"></i></span>
          <span class="text">Message</span>
        </a>
      </li>
      <li class="list">
        <a href="#">
          <span class="icon"><i class="fas fa-camera-retro"></i></span>
          <span class="text">Photos</span>
        </a>
      </li>
      <li class="list">
        <a href="#">
          <span class="icon"><i class="fas fa-cog"></i></span>
          <span class="text">Settings</span>
        </a>
      </li>
      <div class="indicador"></div>
    </ul>
  </div>
  <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.4.2/css/all.min.css" integrity="sha512-z3gLpd7yknf1YoNbCzqRKc4qyor8gaKU1qmn+CShxbuBusANI9QpRohGBreCFkKxLhei6S9CQXFEbbKuqLg0DA==" crossorigin="anonymous" referrerpolicy="no-referrer" />
</body>
