import { Component } from '@angular/core';

@Component({
  selector: 'app-barra',
  templateUrl: './barra.component.html',
  styleUrls: ['./barra.component.css']
})
export class BarraComponent {
  items: Array<{ icon: any; text: string }> = [
    { icon: 'fas fa-home', text: 'Home' },
    { icon: 'fas fa-user', text: 'Profile' },
    { icon: 'fas fa-comment', text: 'Message' },
    { icon: 'fas fa-camera-retro', text: 'Photos' },
    { icon: 'fas fa-cog', text: 'Settings' }
  ];

  activeIndex: number = 0;

  setActive(index: number) {
    this.activeIndex = index;
  }
}

<body>
  <div class="navigation">
    <ul>
      <li *ngFor="let item of items; let i = index" class="list" [ngClass]="{ active: i === activeIndex }" (click)="setActive(i)">
        <a href="#">
          <span class="icon"><i [class]="item.icon"></i></span>
          <span class="text">{{ item.text }}</span>
        </a>
      </li>
      <div class="capa"></div>
      <div class="indicador"></div>
    </ul>
  </div>
  <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.4.2/css/all.min.css" integrity="sha512-z3gLpd7yknf1YoNbCzqRKc4qyor8gaKU1qmn+CShxbuBusANI9QpRohGBreCFkKxLhei6S9CQXFEbbKuqLg0DA==" crossorigin="anonymous" referrerpolicy="no-referrer" />
<link rel="stylesheet" href="./barra.component.css">
</body>
