import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-certificadas',
  templateUrl: './certificadas.component.html',
  styleUrls: ['./certificadas.component.css']
})
export class CertificadasComponent implements OnInit {
  public _listacumplir: Cumplir[]=[]
  get listacumplir(): Cumplir[] {
    return this._listacumplir;
  }
  ngOnInit(){
    this.getcumplir();
  }

  getcumplir(){
    this._listacumplir=[
      {id:1, name:"No obliga a firmar la renuncia", isselected:false},
      {id:2, name:"Paga puntual los sueldos", isselected:false},
      {id:3, name:"Entrega Uniformes", isselected:false},
      {id:4, name:"Entrega Chalecos balisticos", isselected:false},
      {id:5, name:"Está asegurada la compañia", isselected:false}
    ];
  }

}

class Cumplir {
  id:number;
  name:string;
  isselected:boolean;

constructor(){
  this.id=1
  this.name='B&B Segurity'
  this.isselected=false
}

}
