import { Component, OnInit } from '@angular/core';


@Component({
  selector: 'app-certificadas',
  templateUrl: './certificadas.component.html',
  styleUrls: ['./certificadas.component.css']
})


export class CertificadasComponent implements OnInit {
  public _listacumplir: Cumplir[]=[]
red: any;
  get listacumplir(): Cumplir[] {
    return this._listacumplir;
  }
  ngOnInit(){
    this.getcumplir();
  }

  getcumplir(){
    this._listacumplir=[
      {id:1, name:"No obliga a firmar la renuncia", isselected:false},
      {id:2, name:"Paga puntual los sueldos", isselected:false},
      {id:3, name:"Entrega Uniformes", isselected:false},
      {id:4, name:"Entrega Chalecos balisticos", isselected:false},
      {id:5, name:"Está asegurada la compañia", isselected:false}
    ];
  }

onchange(){
  console.log(this._listacumplir);
}


}

class Cumplir {
  id:number;
  name:string;
  isselected:boolean;

constructor(){
  this.id=1
  this.name='B&B Segurity'
  this.isselected=false
}

}

console.log("this._listacumplir");

<html lang="es">
    <head>
        <meta charset="UTF-8">
        <link rel="stylesheet" href="certificadas.component.css">
    </head>
    <body>
   <form class="formulario">
        <div class="container">
            <div class="row">
                <label>Parámetros que la Federación, observa para Certificar a las compañias de Seguirdad</label>
            </div>
            <div *ngFor="let item of _listacumplir" class="row">
                <div class="col-8 col-chk">
                    <input type="checkbox" name="{{item.name}}" (change)="onchange()">
                </div>
                <div class="col-35">
                    <label>
                        {{item.name}}
                    </label>
                </div>
            </div>
        </div>
    </form>
</body>
</html>
